Scripts.OnMouseUp:
if ( this.isMoving ) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end

Scripts.OnMouseDown:
if ( ( ( not this.isLocked ) or ( this.isLocked == 0 ) ) and ( arg1 == "LeftButton" ) ) then
    this:StartMoving();
    this.isMoving = true;
end

Scripts.OnHide:
if ( this.isMoving ) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end

